import { Component } from "react";
import {
    Form, Button, Input, Upload, message
} from "antd";
import { UploadOutlined } from "@ant-design/icons";
import "./index.css";

const booksData = [
    {
        "id": 18308,
        "title": "三体2：黑暗森林",
        "author": "刘慈欣",
        "number": 181,
        "category": 1,
        "cover": "https://wfqqreader-1252317822.image.myqcloud.com/cover/526/183526/s_183526.jpg",
        "intro": "庞大的三体舰队杀气腾腾直扑太阳系，尖端科技被锁死的地球人面对前所未有的危局，面对地球文明前所未有的危局，人类组建起同样庞大的太空舰队，同时（PDC）利用三体人思维透明的致命缺陷，制订了“面壁计划”。在这场你死我活的文明生存竞争中，罗辑探索出了宇宙文明间的黑暗森林法则。借助于这一发现，他以向全宇宙公布三体世界的位置坐标相威胁，暂时制止了三体对太阳系的入侵，使地球与三体建立起脆弱的战略平衡。"
    },
    {
        "id": 18078,
        "title": "三体前传：球状闪电",
        "author": "刘慈欣",
        "number": 34,
        "category": 1,
        "cover": "https://wfqqreader-1252317822.image.myqcloud.com/cover/39/25504039/s_25504039.jpg",
        "intro": "没有《球状闪电》，就没有后来的《三体》！《三体》前传！亚洲首位雨果奖得主刘慈欣的三大长篇之一！（《三体》《球状闪电》《超新星纪元》）《球状闪电》拥有最狂野的想象力！带你从另一个维度观察世界！全面展现刘慈欣对人生的终极思考！《三体》中解决可控核聚变的顶级物理学家丁仪，正是《球状闪电》中找到球状闪电的关键人物。《三体》中一号面壁人的幽灵军队计划，正是来自于《球状闪电》。《球状闪电》中的疑似宇宙观察者，为《三体》中的智子出现铺陈了线索。《球状闪电》和《三体》是刘慈欣两个不同时期的巅峰之作，《球状闪电》是他对人生的终极思考，《三体》则是他对宇宙的终极思考。过一个美妙的人生并不难，关键在于你迷上的是什么。某个离奇的雨夜，一颗球状闪电在一瞬间将少年的父母化为灰烬，而他们身下的板凳却分毫无损。从此少年踏上了研究球状闪电的旅程，死亡者的幽灵笔迹、前苏联的地下科技城、次世代的世界大战……球状闪电意外成为了战争中决定祖国存亡的终极武器！一个从未有人想像过的未来，在宇宙观察者的注视下，悄然降临在人类面前……"
    }
]

export default class Book extends Component {
    constructor() {
        super();
        this.state = {
            display: "find",
            props: {
                name: 'file',
                action: 'https://www.mocky.io/v2/5cc8019d300000980a055e76',
                headers: {
                    authorization: 'authorization-text',
                },
                onChange(info) {
                    if (info.file.status !== 'uploading') {
                        console.log(info.file, info.fileList);
                    }
                    if (info.file.status === 'done') {
                        message.success(`${info.file.name} file uploaded successfully`);
                    } else if (info.file.status === 'error') {
                        message.error(`${info.file.name} file upload failed.`);
                    }
                },
            }
        }
    }

    handleAddBook = () => {
        this.setState({
            display: "add"
        })
    }

    handlePressEnter = (event) => {
        const { target: { value } } = event;
        this.setState({
            display: "resive"
        })
        console.log(value);
    }

    handleConfirm = (value) => {
        console.log(value);
    }

    render() {
        const {
            state: {
                display, props
            },
            handlePressEnter, handleAddBook,
            handleConfirm
        } = this;
        return (
            <div className="content">
                {(() => {
                    switch(display)
                    {
                        case "find": return <>
                            <div className="find">书籍名查找</div>
                                <Input
                                    onPressEnter={handlePressEnter}
                                    placeholder="请输入书籍名"
                                    className="input-bookname"
                                />
                                <div
                                    className="add-book"
                                    onClick={handleAddBook}
                                >添加书籍</div>
                        </>;
                        case "add": return <Form
                            labelCol={{ span: 6 }}
                            wrapperCol={{ span: 17 }}
                            onFinish={handleConfirm}
                            autoComplete="off"
                        >
                            <Form.Item
                                label="书籍名"
                                name="bookname"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input bookname!'
                                        }
                                    ]
                                }
                            >
                                <Input className="input-resive" />
                            </Form.Item>
                            <Form.Item
                                label="作者"
                                name="author"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input author!'
                                        }
                                    ]
                                }
                            >
                                <Input className="input-resive" />
                            </Form.Item>
                            <Form.Item
                                label="剩余数量"
                                name="number"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input number!'
                                        }
                                    ]
                                }
                            >
                                <Input className="input-resive" />
                            </Form.Item>
                            <Form.Item
                                label="简介"
                                name="brief"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input brief!'
                                        }
                                    ]
                                }
                            >
                                <Input className="input-resive" />
                            </Form.Item>
                            <Upload {...props}>
                                <Button 
                                    className="upload"
                                    icon={<UploadOutlined />}
                                >点击上传图片</Button>
                            </Upload>
                            <Form.Item wrapperCol={{ offset: 6, span: 17 }}>
                                <Button
                                    type="primary"
                                    htmlType="submit"
                                >
                                    确认添加
                                </Button>
                            </Form.Item>
                        </Form>;
                        case "resive": return <div className="page">
                            {
                                booksData.map((item) => {
                                    return <div className="page-item" key={item.id}>
                                        <div className="item-left">
                                            <img className="item-image" src={item.cover} />
                                        </div>
                                        <div className="item-right">
                                            <div className="item-title">
                                                <div className="title-word">{item.title}</div>
                                                <div className="title-btn">
                                                    <Button>删除</Button>
                                                    <Button>修改</Button>
                                                </div>
                                            </div>
                                            <div className="item-author">{item.author}</div>
                                            <div className="item-number">数目: {item.number}</div>
                                            <div className="item-brief">{item.intro}</div>
                                            <div className="item-category">分类：{item.category}</div>
                                        </div>
                                    </div>
                                })
                            }
                        </div>;
                    }
                })()}
            </div>
        )
    }
}
