import { Component } from "react";
import { Modal } from "antd";
import User from "./User/index";
import Book from "./Book/index";
import "./index.css";

export default class Person extends Component {
    constructor() {
        super();
        this.state = {
            isVisible: false,
            display: "find",
            // true 用户管理
            manageKind: true
        }
    }

    showModal = () => {
        this.setState({
            isVisible: true
        });
    }

    handleOk = () => {
        this.setState({
            isVisible: false
        });
        console.log("点击确定");
    }

    handleCancel = () => {
        this.setState({
            isVisible: false
        });
        console.log("点击取消")
    }

    handleSwitch = () => {
        const { manageKind } = this.state;

        this.setState({
            manageKind: !manageKind
        })
    }

    render() {
        const {
            state: {
                isVisible, manageKind
            },
            showModal, handleOk,
            handleCancel, handleSwitch
        } = this;

        return(
            <div className="person">
                <Modal
                    title="修改密码"
                    visible={isVisible}
                    onOk={handleOk}
                    onCancel={handleCancel}
                    okText="确定"
                    cancelText="取消"
                >
                    <p>弹窗</p>
                </Modal>
                <div className="left">
                    <div className="username">布丁</div>
                    <div className="email">2245769640@qq.com</div>
                    <div
                        onClick={showModal}
                        className="password"
                    >修改密码</div>
                </div>
                <div className="right">
                    <div
                        onClick={handleSwitch}
                        className="switch"
                    >
                        {manageKind ? "用户管理" : "书籍管理"}
                    </div>
                    <div className="title">用户管理</div>
                    <div className="bar"></div>
                    {manageKind ? <User /> : <Book /> }
                </div>
            </div>
        )
    }
}
