import { Component } from "react";
import { Input, Form, Button } from "antd";
import "./index.css";

const { Password: InputPassword } = Input;

export default class User extends Component {
    constructor() {
        super();
        this.state = {
            display: "find"
        }
    }

    handlePressEnter = (event) => {
        const { target: { value } } = event;
        this.setState({
            display: "resive"
        })
        console.log(value);
    }

    handleAddUser = () => {
        this.setState({
            display: "add"
        })
    }

    handleConfirm = (value) => {
        const { display } = this.state;

        if (display === "add") {
            console.log("add", value);
        } else if (display === "resive") {
            console.log("resive", value);
        }
    }

    handleDeleteUser = () => {
        console.log("删除该用户")
    }

    render() {
        const {
            state: {
                display
            },
            handleDeleteUser, handlePressEnter,
            handleAddUser, handleConfirm
        } = this;

        return (
            <div className="content">
                {(() => {
                    switch(display)
                    {
                        case "find": return <>
                            <div className="find">用户名查找</div>
                                <Input
                                    onPressEnter={handlePressEnter}
                                    placeholder="请输入用户名"
                                    className="input-username"
                                />
                                <div
                                    className="add-user"
                                    onClick={handleAddUser}
                                >添加用户</div>
                            </>;
                        case "add": return <Form
                            labelCol={{ span: 6 }}
                            wrapperCol={{ span: 17 }}
                            onFinish={handleConfirm}
                            autoComplete="off"
                        >
                            <Form.Item
                                label="用户名"
                                name="username"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input username!'
                                        }
                                    ]
                                }
                            >
                                <Input className="input-resive" />
                            </Form.Item>
                            <Form.Item
                                label="密码"
                                name ="password"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input password!'
                                        }
                                    ]
                                }
                            >
                                <InputPassword className="input-resive" />
                            </Form.Item>
                            <Form.Item
                                label="确认密码"
                                name="rePassword"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input password!'
                                        }
                                    ]
                                }
                            >
                                <InputPassword className="input-resive" />
                            </Form.Item>
                            <Form.Item
                                label="邮箱"
                                name="email"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input email!'
                                        }
                                    ]
                                }
                            >
                                <Input className="input-resive" />
                            </Form.Item>
                            <Form.Item wrapperCol={{ offset: 6, span: 17 }}>
                                <Button
                                    type="primary"
                                    htmlType="submit"
                                >
                                    确认添加
                                </Button>
                            </Form.Item>
                        </Form>;
                        case "resive": return <Form
                            labelCol={{ span: 6 }}
                            wrapperCol={{ span: 17 }}
                            onFinish={handleConfirm}
                            autoComplete="off"
                            initialValues={{
                                "username": "布丁",
                                "password": "2245769640",
                                "rePassword": "2245769640",
                                "email": "2245769640@qq.com"
                            }}
                        >
                            <Form.Item
                                label="用户名"
                                name="username"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input your username!'
                                        }
                                    ]
                                }
                            >
                                <Input className="input-resive" />
                            </Form.Item>
                            <Form.Item
                                label="密码"
                                name ="password"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input your password!'
                                        }
                                    ]
                                }
                            >
                                <Input className="input-resive" />
                            </Form.Item>
                            <Form.Item
                                label="确认密码"
                                name="rePassword"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input your password!'
                                        }
                                    ]
                                }
                            >
                                <Input className="input-resive" />
                            </Form.Item>
                            <Form.Item
                                label="邮箱"
                                name="email"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input your email!'
                                        }
                                    ]
                                }
                            >
                                <Input className="input-resive" />
                            </Form.Item>
                            <Form.Item wrapperCol={{ offset: 6, span: 17 }}>
                                <Button
                                    type="primary"
                                    htmlType="submit"
                                >
                                    确认修改
                                </Button>
                            </Form.Item>
                            <Button
                                className="delete"
                                type="primary"
                                onClick={handleDeleteUser}
                                danger
                            >
                                删除该用户
                            </Button>
                        </Form>;
                    }
                })()
                }
            </div>
        )
    }
}
