import axios from "axios";
import { message }  from "antd";

export default function requestJS(url, config) {
    let {
        method = "GET",
        params = {},
        data = {},
        timeout = 3000
    } = config;

    return new Promise((resolve, reject) => {
        axios({
            url,
            method,
            params,
            data,
            timeout,
            headers: {
                "Content-Type": "application/json",
                "token": window.sessionStorage.getItem("token") || ""
            }
        }).then(response => {
            if (response) {
                if (response.data && response.data.code) {
                    resolve(response);
                } else {
                    message.error("返回的数据格式有误");
                    resolve(response);
                }
            } else {
                message.error("服务器错误");
                resolve(response)
            }
        }).catch(error => {
            message.error("网络异常，请稍后重试");
            reject(error);
        })
    })
}
