import { Component } from "react";
import {
    Form, Input,
    Button,
} from "antd";
import "./index.css";

const FormItem = Form.Item;
const { Password: InputPassword } = Input;

export default class Login extends Component {
    constructor() {
        super();
        this.state = {
            // true 为登陆
            status: true
        }
    }

    handleRegister = () => {
        this.setState({
            status: !this.state.status
        })
    }

    handleClickLogin = (value) => {
        console.log(value);
    }

    handleClickRegister = (value) => {
        console.log(value);
        console.log(this.formRef);
        // this.formRef.current.resetFields();
    }

    formRf

    render() {
        const { 
            state: { status },
            handleRegister,
            handleClickLogin,
            handleClickRegister
        } = this;

        return (
            <div className="login">
                <div className="content">
                    <div className="header">
                        <div>图书管理系统</div>
                    </div>
                    {status
                        ? (<Form
                            name="basic"
                            labelCol={{ span: 6 }}
                            wrapperCol={{ span: 17 }}
                            initlalValues={{ remember: true }}
                            onFinish={handleClickLogin}
                            autoComplete="off"
                        >
                            <FormItem
                                label="用户名"
                                name="username"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input your username!'
                                        }
                                    ]
                                }
                            >
                                <Input />
                            </FormItem>
                            <FormItem
                                label="密码"
                                name="password"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input your password!'
                                        }
                                    ]
                                }
                            >
                                <InputPassword />
                            </FormItem>
                            <FormItem
                                wrapperCol={{ offset: 6, span: 17 }}
                            >
                                <Button
                                    type="primary"
                                    htmlType="submit"
                                >
                                    登陆
                                </Button>
                            </FormItem>
                        </Form>)
                        : (<Form
                            name="basic"
                            labelCol={{ span: 6 }}
                            wrapperCol={{ span: 17 }}
                            ref={this.formRef}
                            initlalValues={{ remember: true }}
                            onFinish={handleClickRegister}
                            autoComplete="off"
                        >
                            <FormItem
                                label="用户名"
                                name="username"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input your username!'
                                        }
                                    ]
                                }
                            >
                                <Input />
                            </FormItem>
                            <FormItem
                                label="密码"
                                name="password"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input your password!'
                                        }
                                    ]
                                }
                            >
                                <InputPassword />
                            </FormItem>
                            <FormItem
                                label="确认密码"
                                name="rePassword"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input your password!'
                                        }
                                    ]
                                }
                            >
                                <InputPassword />
                            </FormItem>
                            <FormItem
                                label="邮箱"
                                name="email"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input your password!'
                                        }
                                    ]
                                }
                            >
                                <Input />
                            </FormItem>
                            <FormItem
                                wrapperCol={{ offset: 6, span: 17 }}
                            >
                                <Button
                                    type="primary"
                                    htmlType="submit"
                                >
                                    注册
                                </Button>
                            </FormItem>
                        </Form>)}
                    <div className="footer">
                        <a
                            className="register"
                            onClick={handleRegister}
                        >{status ? '注册' : '登陆'}</a>
                    </div>
                </div>
            </div>
        )
    }
}
