import React, { useState } from "react";
import { Modal } from "antd";
import { observer } from "mobx-react-lite";
import User from "./User/index";
import Book from "./Book/index";
import "./index.css";
import myStore from "../../store";

const { confirm } = Modal;

function Person() {
    const [manageKind, setManageKind] = useState(true);
    const userName = localStorage.getItem("username");
    const email = localStorage.getItem("email")

    const handleSwitch = () => {
        console.log(myStore.switchStatus);
        if (myStore.switchStatus) {
            confirm({
                title: "图书管理系统",
                content: "是否确认中止当前操作",
                okText: "确认",
                cancelText: "取消",
                onOk() {
                    setManageKind(!manageKind);
                    myStore.getUsersRequest();
                }
            })
        } else {
            setManageKind(!manageKind);
            myStore.getUsersRequest();
        }
    }

    return (
        <div className="person">
            <div className="left">
                <div className="username">{userName}</div>
                <div className="email">{email}</div>
            </div>
            <div className="right">
                <div
                    onClick={handleSwitch}
                    className="switch"
                >
                    {manageKind ? "书籍管理" : "用户管理"}
                </div>
                <div className="title">
                    {manageKind ? "用户管理" : "书籍管理"}
                </div>
                <div className="bar"></div>
                {manageKind ? <User /> : <Book />}
            </div>
        </div>
    )
}

export default observer(Person);
