import React, { Component } from "react";
import { Modal, Form, Input } from "antd";
import User from "./User/index";
import Book from "./Book/index";
import "./index.css";

const { Password: InputPassword } = Input;

export default class Person extends Component {
    formRef = React.createRef()

    constructor() {
        super();
        this.state = {
            isVisible: false,
            display: "find",
            // true 用户管理
            manageKind: true,
            newPassword: "",
            rePassword: ""
        }
    }

    showModal = () => {
        this.setState({
            isVisible: true
        });
    }

    handleOk = () => {
        this.setState({
            isVisible: false
        });
        this.formRef.current.resetFields();
        const {
            state: {
                newPassword, rePassword
            }
        } = this;
        console.log(newPassword, rePassword);
    }

    handleCancel = () => {
        this.setState({
            isVisible: false
        });
        this.formRef.current.resetFields();
    }

    handleSwitch = () => {
        const { manageKind } = this.state;

        this.setState({
            manageKind: !manageKind
        })
    }

    handleNewPassword = (event) => {
        const { target: { value } } = event;
        this.setState({
            newPassword: value
        })
    }

    handleRePassword = (event) => {
        const { target: { value } } = event;
        this.setState({
            rePassword: value
        })
    }

    render() {
        const {
            state: {
                isVisible, manageKind,
                rePassword, newPassword
            },
            showModal, handleOk,
            handleCancel, handleSwitch,
            handleNewPassword
        } = this;

        return(
            <div className="person">
                <Modal
                    title="修改密码"
                    visible={isVisible}
                    onOk={handleOk}
                    onCancel={handleCancel}
                    okText="确定"
                    cancelText="取消"
                >
                    <Form
                        labelCol={{ span: 6 }}
                        wrapperCol={{ span: 17 }}
                        autoComplete="off"
                        ref={this.formRef}
                    >
                        <Form.Item
                            label="新密码"
                            name="newPassword"
                            rules={
                                [
                                    {
                                        required: true,
                                        message: 'Please input your password!'
                                    }
                                ]
                            }
                        >
                            <InputPassword
                                value={newPassword}
                                onChange={handleNewPassword}
                            />
                        </Form.Item>
                        <Form.Item
                            label="确认密码"
                            name="rePassword"
                            rules={
                                [
                                    {
                                        required: true,
                                        message: 'Please input your password!'
                                    }
                                ]
                            }
                        >
                            <InputPassword
                                value={rePassword}
                            />
                        </Form.Item>
                    </Form>
                </Modal>
                <div className="left">
                    <div className="username">布丁</div>
                    <div className="email">2245769640@qq.com</div>
                    <div
                        onClick={showModal}
                        className="password"
                    >修改密码</div>
                </div>
                <div className="right">
                    <div
                        onClick={handleSwitch}
                        className="switch"
                    >
                        {manageKind ? "书籍管理" : "用户管理"}
                    </div>
                    <div className="title">
                        {manageKind ? "用户管理" : "书籍管理"}
                    </div>
                    <div className="bar"></div>
                    {manageKind ? <User /> : <Book /> }
                </div>
            </div>
        )
    }
}
