import { Component } from "react";
import { Modal, Input } from "antd";
import "./index.css";

export default class Person extends Component {
    constructor() {
        super();
        this.state = {
            isVisible: false
        }
    }

    showModal = () => {
        this.setState({
            isVisible: true
        });
    }

    handleOk = () => {
        this.setState({
            isVisible: false
        });
        console.log("点击确定");
    }

    handleCancel = () => {
        this.setState({
            isVisible: false
        });
        console.log("点击取消")
    }

    render() {
        const { showModal, handleOk, handleCancel } = this;
        const { isVisible } = this.state;
        return(
            <div className="person">
                <Modal
                    title="修改密码"
                    visible={isVisible}
                    onOk={handleOk}
                    onCancel={handleCancel}
                    okText="确定"
                    cancelText="取消"
                >
                    <p>弹窗</p>
                </Modal>
                <div className="left">
                    <div className="username">布丁</div>
                    <div className="email">2245769640@qq.com</div>
                    <div
                        onClick={showModal}
                        className="password"
                    >修改密码</div>
                </div>
                <div className="right">
                    <div className="switch">书籍管理</div>
                    <div className="title">用户管理</div>
                    <div className="bar"></div>
                    <div className="content">
                        <div className="find">用户名查找</div>
                        <Input placeholder="请输入用户名" />
                    </div>
                </div>
            </div>
        )
    }
}
