import { Component } from "react";
import {
    Form, Button, Input, Upload
} from "antd";
import { UploadOutlined } from "@ant-design/icons";
import "./index.css";

export default class Book extends Component {
    constructor() {
        super();
        this.state = {
            display: "find"
        }
    }

    handleAddBook = () => {
        this.setState({
            display: "add"
        })
    }

    handlePressEnter = (event) => {
        const { target: { value } } = event;
        this.setState({
            display: "resive"
        })
        console.log(value);
    }

    handleConfirm = (value) => {
        console.log(value);
    }

    render() {
        const {
            state: {
                display
            },
            handlePressEnter, handleAddBook,
            handleConfirm
        } = this;
        return (
            <div className="content">
                {(() => {
                    switch(display)
                    {
                        case "find": return <>
                            <div className="find">书籍名查找</div>
                                <Input
                                    onPressEnter={handlePressEnter}
                                    placeholder="请输入书籍名"
                                    className="input-bookname"
                                />
                                <div
                                    className="add-book"
                                    onClick={handleAddBook}
                                >添加书籍</div>
                        </>;
                        case "add": return <Form
                            labelCol={{ span: 6 }}
                            wrapperCol={{ span: 17 }}
                            onFinish={handleConfirm}
                            autoComplete="off"
                        >
                            <Form.Item
                                label="书籍名"
                                name="bookname"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input bookname!'
                                        }
                                    ]
                                }
                            >
                                <Input className="input-resive" />
                            </Form.Item>
                            <Form.Item
                                label="作者"
                                name="author"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input author!'
                                        }
                                    ]
                                }
                            >
                                <Input className="input-resive" />
                            </Form.Item>
                            <Form.Item
                                label="剩余数量"
                                name="number"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input number!'
                                        }
                                    ]
                                }
                            >
                                <Input className="input-resive" />
                            </Form.Item>
                            <Form.Item
                                label="简介"
                                name="brief"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input brief!'
                                        }
                                    ]
                                }
                            >
                                <Input className="input-resive" />
                            </Form.Item>
                            <Upload {...props}>
                                <Button icon={<UploadOutlined />}>点击上传</Button>
                            </Upload>
                            {/* <Form.Item
                                label="图片"
                                name="image"
                                rules={
                                    [
                                        {
                                            required: true,
                                            message: 'Please input image!'
                                        }
                                    ]
                                }
                            >
                                <Input />
                            </Form.Item> */}
                        </Form>;
                        case "resive": return <></>;
                    }
                })()}
            </div>
        )
    }
}
