import { Component } from "react";
import {
    Form, Button, Input
} from "antd";
import "./index.css";

export default class Book extends Component {
    constructor() {
        super();
        this.state = {
            display: "find"
        }
    }

    handleAddBook = () => {
        console.log("添加书籍");
    }

    handlePressEnter = (event) => {
        const { target: { value } } = event;
        this.setState({
            display: "resive"
        })
        console.log(value);
    }

    render() {
        const {
            state: {
                display
            },
            handlePressEnter, handleAddBook
        } = this;
        return (
            <div className="content">
                {(() => {
                    switch(display)
                    {
                        case "find": return <>
                            <Input
                                onPressEnter={handlePressEnter}
                                placeholder="请输入书籍名"
                                className="input-bookname"
                            />
                            <div
                                className="add-book"
                                onClick={handleAddBook}
                            >添加书籍</div>
                        </>;
                        case "add": return <></>;
                        case "resive": return <></>;
                    }
                })()}
            </div>
        )
    }
}
