import { Component } from "react";
import { Modal } from "antd";
import "./index.css";

export default class Person extends Component {
    constructor() {
        super();
        this.state = {
            isVisible: false
        }
    }

    showModal = () => {
        console.log(this.state.isVisible)
        this.setState({
            isVisible: true
        });
    }

    handleOk = () => {
        this.setState({
            isVisible: true
        });
        console.log("点击确定");
    }

    handleCancel = () => {
        this.setState({
            isVisible: true
        });
        console.log("点击取消")
    }

    render() {
        const { showModal, isVisible, handleOk, handleCancel } = this.state;

        return(
            <div className="person">
                <Modal
                    title="修改密码"
                    visible={isVisible}
                    onOk={handleOk}
                    onCancel={handleCancel}
                >
                    <p>弹窗</p>
                </Modal>
                <div className="left">
                    <div className="username">布丁</div>
                    <div className="email">2245769640@qq.com</div>
                    <div
                        onClick={showModal}
                        className="password"
                    >修改密码</div>
                </div>
                <div className="right"></div>
            </div>
        )
    }
}
