import { Component } from "react";
import { Menu, Dropdown } from "antd";
import "./index.css";

const menu = (
    <Menu style={{ color: "red"}}>
        <Menu.Item
            className="menu-item"
            key="1"
            onClick={this.borrowBook}
        >借阅书籍</Menu.Item>
        <Menu.Item 
            className="menu-item"
            key="2"
            onClick={this.returnBook}
        >归还书籍</Menu.Item>
    </Menu>
);

export default class Home extends Component {
    returnBook = () => {
        console.log("归还书籍");
    }

    borrowBook = () => {
        console.log("借阅书籍");
    }

    render() {
        return (
            <div className="back">
                <div className="top-left">图书管理系统</div>
                <div className="top-person">
                    <Dropdown
                        placement="topLeft"
                        overlay={menu}
                        trigger="hover"
                    >
                        <p>用户名</p>
                    </Dropdown>
                </div>
            </div>
        )
    }
}
