import { Component } from "react";
import { Menu, Dropdown } from "antd";
import "./index.css";

export default class Home extends Component {

    returnBook = () => {
        console.log("归还书籍");
    }

    borrowBook = () => {
        console.log("借阅书籍");
    }

    presonCenter = () => {
        console.log("个人中心");
        console.log(this.props.history)
        this.props.history.push({
            pathname: '/person'
        })
    }

    render() {
        const {
            returnBook, borrowBook, presonCenter
        } = this;

        return (
            <div className="back">
                <div className="top-left">图书管理系统</div>
                <div className="top-person">
                    <Dropdown
                        placement="topLeft"
                        overlay={
                        <Menu style={{ color: "red"}}>
                            <Menu.Item
                                className="menu-item"
                                key="1"
                                onClick={borrowBook}
                            >
                                借阅书籍
                            </Menu.Item>
                            <Menu.Item 
                                className="menu-item"
                                key="2"
                                onClick={returnBook}
                            >
                                归还书籍
                            </Menu.Item>
                            <Menu.Item 
                                className="menu-item"
                                key="3"
                                onClick={presonCenter}
                            >
                                个人中心
                            </Menu.Item>
                        </Menu>
                        }
                        trigger="hover"
                    >
                        <p>用户名</p>
                    </Dropdown>
                </div>
            </div>
        )
    }
}
