import { Component } from "react";
import { Modal, Input, Form } from "antd";
import "./index.css";

export default class Person extends Component {
    constructor() {
        super();
        this.state = {
            isVisible: false
        }
    }

    showModal = () => {
        this.setState({
            isVisible: true
        });
    }

    handleOk = () => {
        this.setState({
            isVisible: false
        });
        console.log("点击确定");
    }

    handleCancel = () => {
        this.setState({
            isVisible: false
        });
        console.log("点击取消")
    }

    handlePressEnter = (event) => {
        const { target: { value } } = event;
        console.log(value);
    }

    handleAddUser = () => {
        console.log("添加用户")
    }

    render() {
        const {
            state: { isVisible },
            showModal, handleOk,
            handleCancel, handlePressEnter,
            handleAddUser
        } = this;

        return(
            <div className="person">
                <Modal
                    title="修改密码"
                    visible={isVisible}
                    onOk={handleOk}
                    onCancel={handleCancel}
                    okText="确定"
                    cancelText="取消"
                >
                    <p>弹窗</p>
                </Modal>
                <div className="left">
                    <div className="username">布丁</div>
                    <div className="email">2245769640@qq.com</div>
                    <div
                        onClick={showModal}
                        className="password"
                    >修改密码</div>
                </div>
                <div className="right">
                    <div className="switch">书籍管理</div>
                    <div className="title">用户管理</div>
                    <div className="bar"></div>
                    <div className="content">
                        {/* <div className="find">用户名查找</div>
                        <Input
                            onPressEnter={handlePressEnter}
                            placeholder="请输入用户名"
                            className="input-username"
                        />
                        <div
                            className="add-user"
                            onClick={handleAddUser}
                        >添加用户</div> */}
                        <Form >
                            <Form.Item label="用户名">
                                <Input className="input-resive" defaultValue="布丁" />
                            </Form.Item>
                            <Form.Item label="密码">
                                <Input className="input-resive" defaultValue="2245769640" />
                            </Form.Item>
                            <Form.Item label="确认密码">
                                <Input className="input-resive" defaultValue="2245769640" />
                            </Form.Item>
                            <Form.Item label="邮箱">
                                <Input className="input-resive" defaultValue="2245769640@qq.com" />
                            </Form.Item>
                        </Form>
                    </div>
                </div>
            </div>
        )
    }
}
