import {
    makeAutoObservable
} from "mobx";
import { message } from "antd";
import {
    booksSearch,
    booksKind,
    login
} from "../services/index.js";

class Mobx {
    booksPageNum = 0
    booksData = []
    booksKindData = []

    constructor() {
        makeAutoObservable(this, {

        });
    }

    login = async(data) => {
        await login(data).then(event => {
            const {
                data: { 
                    code, token,
                    message
                }
            } = event;
            if (!code) {
                console.log(token);
                message.success("登录成功");
            } else {
                message.error(message);
            }
        })
    }

    booksSearch = async(data) => {
        await booksSearch(data).then(event => {
            const { data: { books, num_pages } } = event;
            this.booksData = books;
            this.booksPageNum = num_pages + 1;
        });
    }

    booksSearchAdd = async(data) => {
        if (data.page < this.booksPageNum) {
            await booksSearch(data).then(event => {
                const { data: { books } } = event;
                this.booksData = [...this.booksData, ...books]
            })
        } else {
            message.warning("无更多书籍");
        }
    }

    booksKindRequest = async() => {
        await booksKind().then(event => {
            this.booksKindData = event.data;
        })
    }
}

const myStore = new Mobx();
export default myStore;
