import {
    computed, makeAutoObservable
} from "mobx";
import {
    booksSearch,
    booksKind
} from "../services/index.js";

class Mobx {
    booksPageNum = 1
    booksData = []
    booksKindData = []

    constructor() {
        makeAutoObservable(this, {
            getBooksData: computed
        });
    }

    get getBooksData() {
        return this.booksData;
    }

    booksSearch = async(data) => {
        await booksSearch(data).then(event => {
            const { data: { books } } = event;
            this.booksData = books;
        });
    }

    booksSearchAdd = async(data) => {
        await booksSearch(data).then(event => {
            const { data: { books } } = event;
            this.booksData = [...this.booksData, ...books]
        })
    }

    booksKindRequest = async() => {
        await booksKind().then(event => {
            this.booksKindData = event.data;
        })
    }
}

const myStore = new Mobx();
export default myStore;
