import { Component } from "react";
import {
    Form, Button, Input
} from "antd";
import "./index.css";

export default class Book extends Component {
    constructor() {
        super();
        this.state = {
            display: "find"
        }
    }

    handleAddBook = () => {
        this.setState({
            display: "add"
        })
    }

    handlePressEnter = (event) => {
        const { target: { value } } = event;
        this.setState({
            display: "resive"
        })
        console.log(value);
    }

    handleConfirm = (value) => {
        console.log(value);
    }

    render() {
        const {
            state: {
                display
            },
            handlePressEnter, handleAddBook,
            handleConfirm
        } = this;
        return (
            <div className="content">
                {(() => {
                    switch(display)
                    {
                        case "find": return <>
                            <div className="find">书籍名查找</div>
                                <Input
                                    onPressEnter={handlePressEnter}
                                    placeholder="请输入书籍名"
                                    className="input-bookname"
                                />
                                <div
                                    className="add-book"
                                    onClick={handleAddBook}
                                >添加书籍</div>
                        </>;
                        case "add": return <Form
                            labelCol={{ span: 6 }}
                            wrapperCol={{ span: 17 }}
                            onFinish={handleConfirm}
                            autoComplete="off"
                        >

                        </Form>;
                        case "resive": return <></>;
                    }
                })()}
            </div>
        )
    }
}
