import { Component } from "react";
import {
    Form, Input,
    Button,
} from "antd";
import "./index.css";

const FormItem = Form.Item;
const { Password: InputPassword } = Input;

export default class Login extends Component {
    constructor() {
        super();
        this.state = {
            // true 为登陆
            status: true
        }
    }

    handleRegister = () => {
        console.log("注册");
        this.setState({
            status: !this.state.status
        })
    }

    onFinish = value => {
        console.log('Success: ', value);
    }

    onFinishFailed = errorInfo => {
        console.log('Failed: ', errorInfo);
    }

    render() {
        const { 
            state: { status },
            onFinish, onFinishFailed, handleRegister
        } = this;

        return (
            <div className="login">
                <div className="content">
                    <div className="header">
                        <div>图书管理系统</div>
                    </div>
                    {status ? "登陆" : "注册"}
                    <Form
                        name="basic"
                        labelCol={{ span: 6 }}
                        wrapperCol={{ span: 17 }}
                        initlalValues={{ remember: true }}
                        onFinish={onFinish}
                        onFinishFailed={onFinishFailed}
                        autoComplete="off"
                    >
                        <FormItem
                            label="用户名"
                            name="username"
                            rules={
                                [
                                    {
                                        required: true,
                                        message: 'Please input your username!'
                                    }
                                ]
                            }
                        >
                            <Input />
                        </FormItem>
                        <FormItem
                            label="密码"
                            name="password"
                            rules={
                                [
                                    {
                                        required: true,
                                        message: 'Please input your password!'
                                    }
                                ]
                            }
                        >
                            <InputPassword />
                        </FormItem>
                        <FormItem
                            wrapperCol={{ offset: 6, span: 17 }}
                        >
                            <Button
                                type="primary"
                                htmlType="submit"
                            >
                                登陆
                            </Button>
                        </FormItem>
                    </Form>
                    <div className="footer">
                        <a
                            className="register"
                            onClick={handleRegister}
                        >注册</a>
                    </div>
                </div>
            </div>
        )
    }
}
